ui.UiFactory.customTypes["ui.MessageBacklog"] = {
    "id": "backlogScrollView",
    "type": "ui.DataScrollView",
    "zIndex": 80000,
    "order": 80001,
    "frame": [0, 0, Graphics.width, Graphics.height],
    "params": {
        "dataSource": ($ -> $dataFields.backlog),
        "template": {
            "size": [Graphics.width, 200],
            "descriptor": {
                "type": "ui.FreeLayout",
                "frame": [0, 0, Graphics.width, 200],
                "controls": [
                    {
                        "type": "ui.FreeLayout",
                        "frame": [0, 20, Graphics.width / 4, 30],
                        "controls": [
                            {
                                "type": "ui.Text",
                                "styles": ["backlogNameText"],
                                "frame": [0, 0],
                                "margin": [0, 0, 20, 0],
                                "sizeToFit": true,
                                "alignmentX": "right",
                                "zIndex": 82000,
                                "formulas": [$ ->
                                    c = $dataFields.backlog[o.parent.parent.index].character
                                    o.text = c?.name ? ""
                                    o.font.color.setFromObject(c.textColor) if c?.textColor
                                ],
                                "text": "Name"
                            }
                        ]
                    },
                    {
                        "type": "ui.Text",
                        "styles": ["messageText"],
                        "frame": [Graphics.width / 4 + 20, 20, Graphics.width * 0.7, 0],
                        "sizeToFit": { "horizontal": false, "vertical": true },
                        "formatting": true,
                        "wordWrap": true,
                        "zIndex": 82000,
                        "order": 80001,
                        "formulas": [
                            $ -> o.visible = !$dataFields.backlog[o.parent.index].isChoice
                            $ -> if !$dataFields.backlog[o.parent.index].isChoice then o.text = $dataFields.backlog[o.parent.index].message
                            $ -> if $dataFields.backlog[o.parent.index].isChoice then o.text = $dataFields.backlog[o.parent.index].choice.text
                        ],
                        "text": ""
                    },
                    {
                        "type": "ui.FreeLayout",
                        "alignmentX": 1,
                        "sizeToFit": true,
                        "formulas": [
                            $ -> o.visible = $dataFields.backlog[o.parent.index].isChoice
                        ],
                        "controls":[
                            {
                                "type": "ui.Window",
                                "formulas": [
                                    $ -> o.controls[0].image = "UI/selection.png"
                                ],
                                "frame": [0, 0, 750, 50],
                                "margin": [0, 0, 0, 30],
                                "zIndex": 4999
                            },
                            {
                                "type": "ui.Text",
                                "sizeToFit": true,
                                "styles": ["regularUIText"],
                                "alignmentX": "center",
                                "alignmentY": "center",
                                "frame": [0, 12],
                                "margin": [0, 0, 0, 30]
                                "formulas": [
                                    $ -> if $dataFields.backlog[o.parent.parent.index].isChoice then o.text = $dataFields.backlog[o.parent.parent.index].choice.text
                                ],
                                "zIndex": 5100
                            }
                        ]
                    }
                ],
                "margin": [0, 0, 0, 20]
            }
        }
    },
    "zIndex": 81000
}

ui.UiFactory.customTypes["ui.MessageBacklogBox"] = {
