class ForLoopCommand
    constructor: (params, interpreter) ->
        @params = params
        @interpreter = interpreter
        @index = 0
        @pointer = interpreter.pointer
        @list = @interpreter.listObjectOf(@params.listVariable)

    condition: ->
        result = @index < @list.length
        @interpreter.setNumberValueTo(@params.indexVariable, @index)
        @interpreter.setValueToVariable(@params.elementVariable, @params.elementType, @list[@index])
        @index++

        return result

gs.ForLoopCommand = ForLoopCommand

